import User_card from '../user-card/User_card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { CardTitle, Dropdown, Modal } from 'react-bootstrap';
import Row from 'react-bootstrap/Row';
import { useState } from 'react';

import '../App.css';

function Content() {

  const [add, setAdd] = useState([]);
  function handelEdit(){
    const title = document.getElementById('cardTitel');
    const cardSubtitle = document.getElementById('cardSubtitle');
    const cardText = document.getElementById('cardText');

    let titleInput = document.getElementById('userName').value;
    let userTitle = document.getElementById('userTitle').value;
    let userText = document.getElementById('userText').value;

    title.innerHTML = titleInput;
    cardSubtitle.innerHTML = userTitle;
    cardText.innerHTML = userText;

    const cardsContainer = document.getElementById('cardsContainer');
    cardsContainer.append(<User_card />)
    // setAdd(title);
  };
  return (
    <>
    <Row className="main-content p-5" id="cardsContainer">
      
      
    </Row>
    </>
  );
}

export default Content;
