import User_card from '../user-card/User_card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { CardTitle, Dropdown, Modal } from 'react-bootstrap';
import Row from 'react-bootstrap/Row';
import { useState } from 'react';

import '../App.css';

function Content() {

  const [add, setAdd] = useState("");
  function handelEdit(){
    const title = document.getElementById('cardTitel');
    let titleInput = document.getElementById('userName').val;
    title.text = titleInput.value;
    setAdd(title);
  };
  return (

    <Row className="main-content p-5">
      <User_card />
      <Form>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>user name</Form.Label>
              <Form.Control id="userName" type="text" placeholder="type a name" autoFocus />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlInput2">
              <Form.Label>user title</Form.Label>
              <Form.Control type="text" placeholder="add your title" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
              <Form.Label>user description</Form.Label>
              <Form.Control as="textarea" rows={3} />
          </Form.Group>
          <Button variant="primary" onClick={handelEdit}>
                        Save Changes
           </Button>
      </Form>
    </Row>
  );
}

export default Content;
